import React,{useEffect,useState} from 'react'
import {useDispatch,useSelector} from "react-redux"
import ProductDetailCard from '../components/ProductDetailCard'
import Tabs from '../components/Tabs'
import { fetchProducts, selectAllProducts } from '../store/menu/productsSlice'



const Menu = () => {
  const dispatch = useDispatch()
  const products = useSelector(selectAllProducts )
  const [activeTab,setActiveTab] = useState("")
  const [categories,setCategories] = useState([])
  const allProductsFlat = products.products.flat();
const categoriess = [...new Set(allProductsFlat.map(product => product.category.name))];


  useEffect(()=>{
    dispatch(fetchProducts())
    console.log(`soyi prodcuts pa`,products[0].name)
  },[dispatch])

  return ( 
    <div className='bg-white'>
        {
          products.status === "pending" ?
          <div className='bg-white text-2xl'> Loading ...</div> :
          <div className='menu-wrapper'>  
          {
            products &&
             <Tabs
             list={products?.category.map(product => product.name )} 
             activeTab={activeTab}
             onTabSwitch={setActiveTab}
             /> 
          }
              {
                products && products[2].map ((product, index)=>{
                  return(

                    <ProductDetailCard key={index} product={product} />
                  )
                })
              }

             </div>
        }

      </div>  
  ) 
}

export default Menu
