import React,{useEffect} from 'react'
import {useDispatch,useSelector} from "react-redux"
import { fetchProducts, selectAllProducts } from '../store/menu/productsSlice'


const Menu = () => {
  const dispatch = useDispatch()
  const products = useSelector(selectAllProducts )

  useEffect(()=>{
    dispatch(fetchProducts())
    console.log(`soyi prodcuts pa`,products[1].category)
  },[dispatch])

  return (
    <div>
        {
          products.status === "pending" ?
          <div>Loading ... </div> :
          products && products.map((menuCategory, index)=>{
            return(
              <>
                  <h2 className='bg-white'>{menuCategory.category.name}</h2>
                      <div className="products-list">
                        {
                          menuCategory?.map((product,index)=>{
                            return(
                              <h3 className='bg-green'>{product.name}</h3>

                            )
                          })
                        }
                      </div>
              </>
            )
          })
        }

      </div>  
  ) 
}

export default Menu
