import {createSlice, createAsyncThunk} from "@reduxjs/toolkit"


const initialState = {
    products:[],
    error:null,
    status:"idle"
}

export const productsSlice = createSlice({
    name:"products",
    initialState,
    reducers:{}
}) 



export const {getProducts} = productsSlice.actions
